"use client";
import { Inter } from "next/font/google";
import "./globals.css";
import NavBar from "@/components/views/layout/NavBar";
import SideBar from "@/components/views/layout/SideBar";
import { Toaster } from "@/components/ui/sonner";

import { invoke } from "@tauri-apps/api/tauri";
import { useEffect } from "react";

const inter = Inter({ subsets: ["latin"] });

// export const metadata = {
//   title: "Create Next App",
//   description: "Generated by create next app",
// };

export default function RootLayout({ children }) {
  useEffect(() => {
    const now = new Date();
    const year = now.getFullYear();
    const month = String(now.getMonth() + 1).padStart(2, "0");
    const day = String(now.getDate()).padStart(2, "0");

    const today = `${year}-${month}-${day}`;
    invoke("init_appconfig_table", {
      start_date: today,
    });
    // localStorage.setItem("start_date", `${year}-${month}-${day}`);
  }, []);

  return (
    <html lang="en">
      <body className="flex flex-col h-screen w-screen">
        <NavBar />
        <div className="flex flex-1 overflow-hidden">
          <SideBar />
          <main className={inter.className + " grow overflow-y-auto"}>
            {children}
          </main>
        </div>
        <Toaster />
      </body>
    </html>
  );
}
